mapper.select:
SELECT count(*)
FROM MEMBER
WHERE id = #{memberId}
  AND password = #{password}

mapper.update:
UPDATE MEMBER
SET
    password = #{newPassword}
WHERE id = #{memberId}
  AND password = #{password}

mapper.select:
SELECT *
FROM MEMBER
WHERE name = #{name}
AND email = #{email}

SELECT *, delete_date as deleteDate
FROM MEMBER
WHERE id = #{id}

mapper.update:
UPDATE MEMBER
SET delete_date = NOW()
WHERE id = #{id}
